async def handle_stone(update: Update, context: ContextTypes.DEFAULT_TYPE):
    """–û–±—Ä–∞–±–æ—Ç—á–∏–∫ –í–°–ï–• –∫–∞–º–Ω–µ–π"""
    query = update.callback_query
    await query.answer()
    
    if query.data in STONE_DATA:
        await query.edit_message_text(
            text=STONE_DATA[query.data],
            reply_markup=get_back_button('stones_menu'),
            parse_mode='Markdown'
        )